import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../utils/api';

const Login = () => {
    const [credentials, setCredentials] = useState({ username: '', password: '' });
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const data = await loginUser(credentials);
            localStorage.setItem('token', data.access_token); // Guarda el token en el almacenamiento local
            localStorage.setItem('role', data.role_id); // Guarda el rol del usuario en el almacenamiento local

            // Redirige según el rol del usuario
            if (data.role_id === 1) navigate('/admin'); // Administrador
            else if (data.role_id === 2) navigate('/doctor'); // Doctor
            else if (data.role_id === 3) navigate('/patient'); // Paciente
            else throw new Error('Rol desconocido');
        } catch (error) {
            alert('Error al iniciar sesión. Verifique sus credenciales.');
        }
    };

    return (
        <form onSubmit={handleLogin}>
            <h1>Inicio de Sesión</h1>
            <input
                type="text"
                placeholder="Usuario"
                onChange={(e) => setCredentials({ ...credentials, username: e.target.value })}
            />
            <input
                type="password"
                placeholder="Contraseña"
                onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
            />
            <button type="submit">Iniciar Sesión</button>
        </form>
    );
};

export default Login;
